void init_Rnd_cdm(uint32_t seed){
  int i,j;//,h;
  uint16_t out = 0; //19bit strings
  uint32_t bit_r = 0; // feedback value


  if(seed == 0) seed++;//LFSR = random(0x00000001,0x7FFFFFFF);
  LFSR = seed & 0x7FFFFFFF; // seed is the initial value
  for(j = 0; j < 31; j++){
     out = 0x0000;
     for(i = 0; i < 16; i++){
       out = (out << 1) | (LFSR & 0x00000001);
       bit_r = (LFSR & 0x00000001) ^ ((LFSR & 0x00000080) >> 7);
       LFSR = (LFSR >> 1) | (bit_r << 30);
     }
     reg_a[j] = (out & 0xFFFF);
   }
  n = 0;
  k = 7;
}

uint32_t Rnd_32(){
  uint32_t r_out = 0;
  uint32_t r_bit = 0;
  int i;
  for(i = 0;i < 32;i++){
    reg_a[n] = (reg_a[n] & 0x0007FFFF) ^ (reg_a[k] & 0x0007FFFF);
    r_bit = LUT[reg_a[n] % P]/*pgm_read_byte_near(LUT +( reg_a[n] % P))*/;
    r_out = (r_out << 1) | r_bit;
    n++;
    k++;
    if(n == 31) n = 0;
    else if(k == 31) k = 0;
  }
  return r_out;
}

