static void rijndaelEncrypt(const u32 rk[], uint16_t Nr, const u8 pt[16], u8 ct[16]){
  u32 s0, s1, s2, s3, t0, t1, t2, t3;
#ifndef FULL_UNROLL
  uint16_t r;
#endif /* ?FULL_UNROLL */

  /*
   * map byte array block to cipher state
   * and add initial round key:
   */
   
  s0 = GETU32(pt     ) ^ rk[0];
  s1 = GETU32(pt +  4) ^ rk[1];
  s2 = GETU32(pt +  8) ^ rk[2];
  s3 = GETU32(pt + 12) ^ rk[3];
  
#define ROUND(i,d,s) \
d##0 = TE0(s##0) ^ TE1(s##1) ^ TE2(s##2) ^ TE3(s##3) ^ rk[4 * i]; \
d##1 = TE0(s##1) ^ TE1(s##2) ^ TE2(s##3) ^ TE3(s##0) ^ rk[4 * i + 1]; \
d##2 = TE0(s##2) ^ TE1(s##3) ^ TE2(s##0) ^ TE3(s##1) ^ rk[4 * i + 2]; \
d##3 = TE0(s##3) ^ TE1(s##0) ^ TE2(s##1) ^ TE3(s##2) ^ rk[4 * i + 3]



#ifdef FULL_UNROLL
  ROUND(1,t,s);
  ROUND(2,s,t);
  ROUND(3,t,s);
  ROUND(4,s,t);
  ROUND(5,t,s);
  ROUND(6,s,t);
  ROUND(7,t,s);
  ROUND(8,s,t);
  ROUND(9,t,s);

  if (Nr > 10) {
    ROUND(10,s,t);
    ROUND(11,t,s);

    if (Nr > 12) {
      ROUND(12,s,t);
      ROUND(13,t,s); 

    }
  }
  
      rk += Nr << 2;

#else  /* !FULL_UNROLL */

  /* Nr - 1 full rounds: */
  r = Nr >> 1;
  for (;;) {
    ROUND(1,t,s);
    rk += 8;

    if (--r == 0)
      break;
    ROUND(0,s,t);
  }

#endif /* ?FULL_UNROLL */
#undef ROUND

  /*
   * apply last round and
   * map cipher state to byte array block:
   */
  s0 = TE41(t0) ^ TE42(t1) ^ TE43(t2) ^ TE44(t3) ^ rk[0];
  PUTU32(ct     , s0);
 
  s1 = TE41(t1) ^ TE42(t2) ^ TE43(t3) ^ TE44(t0) ^ rk[1];
  PUTU32(ct +  4, s1);
 
  s2 = TE41(t2) ^ TE42(t3) ^ TE43(t0) ^ TE44(t1) ^ rk[2];
  PUTU32(ct +  8, s2);

  s3 = TE41(t3) ^ TE42(t0) ^ TE43(t1) ^ TE44(t2) ^ rk[3];
  PUTU32(ct + 12, s3);


}
int rijndaelKeySetupEnc(u32 rk[],  const u8 cipherKey[], u16 keyBits){
  u16 i;
  u32 temp;
  
  rk[0] = GETU32(cipherKey     );
  rk[1] = GETU32(cipherKey +  4);
  rk[2] = GETU32(cipherKey +  8);
  rk[3] = GETU32(cipherKey + 12);

  if (keyBits == 128) {
    for (i = 0; i < 10; i++) {
      temp  = rk[3];
      rk[4] = rk[0] ^ TE421(temp) ^ TE432(temp) ^ TE443(temp) ^ TE414(temp) ^ RCON(i);
      rk[5] = rk[1] ^ rk[4];
      rk[6] = rk[2] ^ rk[5];
      rk[7] = rk[3] ^ rk[6];
      rk += 4;

    }
    return 10;
  }

  rk[4] = GETU32(cipherKey + 16);
  rk[5] = GETU32(cipherKey + 20);

  if (keyBits == 192) {
    for (i = 0; i < 8; i++) {
      temp  = rk[5];
      rk[6] = rk[0] ^ TE421(temp) ^ TE432(temp) ^
        TE443(temp) ^ TE414(temp) ^ RCON(i);
      rk[7] = rk[1] ^ rk[6];
      rk[8] = rk[2] ^ rk[7];
      rk[9] = rk[3] ^ rk[8];
      if (i == 7)
        return 12;
      rk[10] = rk[4] ^ rk[9];
      rk[11] = rk[5] ^ rk[10];
      rk += 6;
    }
  }

  rk[6] = GETU32(cipherKey + 24);
  rk[7] = GETU32(cipherKey + 28);

  if (keyBits == 256) {
    for (i = 0; i < 7; i++) {
      temp  = rk[7];
      rk[8] = rk[0] ^ TE421(temp) ^ TE432(temp) ^
        TE443(temp) ^ TE414(temp) ^ RCON(i);
      rk[9] = rk[1] ^ rk[8];
      rk[10] = rk[2] ^ rk[9];
      rk[11] = rk[3] ^ rk[10];
      if (i == 6)
        return 14;
      temp  = rk[11];
      rk[12] = rk[4] ^ TE411(temp) ^ TE422(temp) ^ TE433(temp) ^ TE444(temp);

      rk[13] = rk[5] ^ rk[12];
      rk[14] = rk[6] ^ rk[13];
      rk[15] = rk[7] ^ rk[14];
      rk += 8;
    }
  }

  return -1;
}



void aes_encrypt_deinit(void *ctx){
  os_memset(ctx, 0, AES_PRIV_SIZE);
  os_free(ctx);
}
void aes_encrypt(void *ctx, const u8 *plain, u8 *crypt){
  u32 *rk = (u32*)ctx;

  rijndaelEncrypt((u32*)ctx, rk[AES_PRIV_NR_POS], plain, crypt);
}
void * aes_encrypt_init(const u8 *key, /*size_t*/u16 len){
  u32 *rk;
  int res;
  rk = (u32*)os_malloc(AES_PRIV_SIZE);
  
  if (rk == NULL)
    return NULL;    
   
  res = rijndaelKeySetupEnc(rk, key, len * 8);
  
  if (res < 0) {
    os_free(rk);
    return NULL;
  }
  rk[AES_PRIV_NR_POS] = res;
  
  return rk;
}


static void gf_mulx(u8 *pad)
{
  int i, carry;

  carry = pad[0] & 0x80;
  for (i = 0; i < AES_BLOCK_SIZE - 1; i++)
    pad[i] = (pad[i] << 1) | (pad[i + 1] >> 7);
  pad[AES_BLOCK_SIZE - 1] <<= 1;
  if (carry)
    pad[AES_BLOCK_SIZE - 1] ^= 0x87;
}


/**
 * omac1_aes_vector - One-Key CBC MAC (OMAC1) hash with AES
 * @key: Key for the hash operation
 * @key_len: Key length in octets
 * @num_elem: Number of elements in the data vector
 * @addr: Pointers to the data areas
 * @len: Lengths of the data blocks
 * @mac: Buffer for MAC (128 bits, i.e., 16 bytes)
 * Returns: 0 on success, -1 on failure
 *
 * This is a mode for using block cipher (AES in this case) for authentication.
 * OMAC1 was standardized with the name CMAC by NIST in a Special Publication
 * (SP) 800-38B.
 */
int omac1_aes_vector(const u8 *key, /*size_t*/u16 key_len, /*size_t*/u16 num_elem,
         const u8 *addr[], const /*size_t*/u16 *len, u8 *mac){
  void *ctx;
  u8 cbc[AES_BLOCK_SIZE], pad[AES_BLOCK_SIZE]={};
  const u8 *pos, *end;
  /*size_t*/u16 i, e, left, total_len;
  
  ctx = aes_encrypt_init(key, key_len);
  
  if (ctx == NULL)
    return -1;
    
  os_memset(cbc, 0, AES_BLOCK_SIZE);

  total_len = 0;
  for (e = 0; e < num_elem; e++)
    total_len += len[e];
    
  left = total_len;

  e = 0;
  pos = addr[0];
  end = pos + len[0];

  while (left >= AES_BLOCK_SIZE) {
    for (i = 0; i < AES_BLOCK_SIZE; i++) {
      cbc[i] ^= *pos++;
      if (pos >= end) {
        /*
         * Stop if there are no more bytes to process
         * since there are no more entries in the array.
         */
        if (i + 1 == AES_BLOCK_SIZE &&
            left == AES_BLOCK_SIZE)
          break;
        e++;
        pos = addr[e];
        end = pos + len[e];
      }
    }
    if (left > AES_BLOCK_SIZE)
      aes_encrypt(ctx, cbc, cbc);
      
    left -= AES_BLOCK_SIZE;
  }

  os_memset(pad, 0, AES_BLOCK_SIZE);
  aes_encrypt(ctx, pad, pad);
  gf_mulx(pad);

  if (left || total_len == 0) {
    for (i = 0; i < left; i++) {
      cbc[i] ^= *pos++;
      if (pos >= end) {
        /*
         * Stop if there are no more bytes to process
         * since there are no more entries in the array.
         */
        if (i + 1 == left)
          break;
        e++;
        pos = addr[e];
        end = pos + len[e];
      }
    }
    cbc[left] ^= 0x80;
    gf_mulx(pad);
  }


  for (i = 0; i < AES_BLOCK_SIZE; i++)
    pad[i] ^= cbc[i];

  aes_encrypt(ctx, pad, mac);
  aes_encrypt_deinit(ctx);
  return 0;
}


/**
 * omac1_aes_128_vector - One-Key CBC MAC (OMAC1) hash with AES-128
 * @key: 128-bit key for the hash operation
 * @num_elem: Number of elements in the data vector
 * @addr: Pointers to the data areas
 * @len: Lengths of the data blocks
 * @mac: Buffer for MAC (128 bits, i.e., 16 bytes)
 * Returns: 0 on success, -1 on failure
 *
 * This is a mode for using block cipher (AES in this case) for authentication.
 * OMAC1 was standardized with the name CMAC by NIST in a Special Publication
 * (SP) 800-38B.
 */
int omac1_aes_128_vector(const u8 *key, /*size_t*/u16 num_elem,
       const u8 *addr[], const /*size_t*/u16 *len, u8 *mac){

  return omac1_aes_vector(key, 16, num_elem, addr, len, mac);
}


/**
 * omac1_aes_128 - One-Key CBC MAC (OMAC1) hash with AES-128 (aka AES-CMAC)
 * @key: 128-bit key for the hash operation
 * @data: Data buffer for which a MAC is determined
 * @data_len: Length of data buffer in bytes
 * @mac: Buffer for MAC (128 bits, i.e., 16 bytes)
 * Returns: 0 on success, -1 on failure
 *
 * This is a mode for using block cipher (AES in this case) for authentication.
 * OMAC1 was standardized with the name CMAC by NIST in a Special Publication
 * (SP) 800-38B.
 */
int omac1_aes_128(const u8 *key, const u8 *data, /*size_t*/u16 data_len, u8 *mac){

  return omac1_aes_128_vector(key, 1, &data, &data_len, mac);
}


/**
 * omac1_aes_256 - One-Key CBC MAC (OMAC1) hash with AES-256 (aka AES-CMAC)
 * @key: 256-bit key for the hash operation
 * @data: Data buffer for which a MAC is determined
 * @data_len: Length of data buffer in bytes
 * @mac: Buffer for MAC (128 bits, i.e., 16 bytes)
 * Returns: 0 on success, -1 on failure
 *
 * This is a mode for using block cipher (AES in this case) for authentication.
 * OMAC1 was standardized with the name CMAC by NIST in a Special Publication
 * (SP) 800-38B.
 */
int omac1_aes_256(const u8 *key, const u8 *data, /*size_t*/u16 data_len, u8 *mac){
  return omac1_aes_vector(key, 32, 1, &data, &data_len, mac);
}
